import { React } from "react";

const Slide = (slideData) => {
  const { answers, question } = slideData.data;
  console.log(answers);
  const Answers = () => {
    return (
      <ul>
        {answers.map((answer, index) => (
          <li className="choice" key={index}>
            {answer}
          </li>
        ))}
      </ul>
    );
  };

  return (
    <div className="slide">
      <h3>{question}</h3>
      <ul>
        <Answers />
      </ul>
    </div>
  );
};

export default Slide;
