import { React, useState } from "react";

const attrIbutes = [
  {
    id: 1,
    title: "title 1",
    author: "author text",
    img: "https://m.media-amazon.com/images/I/61JSKZ6vHQS._AC_UY218_.jpg",
  },
  {
    id: 2,
    title: "title 2",
    author: "author text",
    img: "https://m.media-amazon.com/images/I/61JSKZ6vHQS._AC_UY218_.jpg",
  },
  {
    id: 3,
    title: "title string",
    author: "author text",
    img: "https://m.media-amazon.com/images/I/61JSKZ6vHQS._AC_UY218_.jpg",
  },
];

function BookList() {
  const [books, setattrIbutes] = useState(attrIbutes);

  return (
    <section className="booklist">
      {books.map(({ id, img, title, author }, index) => (
        // <Book key={index} {...el} />
        <article key={id} className="book">
          <img src={img} alt={title} onClick={() => alert(title)} />
          <h1
            onMouseOut={(e) => e.target.classList.remove("eee")}
            onMouseEnter={(e) => e.target.classList.add("eee")}
          >
            {title}
          </h1>
          <h4>{author}</h4>
          <button onClick={() => removeItem(id)}>Delete book #{id}</button>
        </article>
      ))}
    </section>
  );
}

ReactDom.render(<BookList />, document.getElementById("root"));
