import React, { useState } from "react";
import Slide from "./slide";
import inputData from "./inputData";

const Quizz = () => {
  const [slide, setSlide] = useState(0);
  const [userAnswer, setUseAnswer] = useState(0);

  const fill = { width: (100 / inputData.length) * (slide + 1) + "%" };

  const updateAnswer = (id) => {
    setUseAnswer(id + 1);
  };

  return (
    <div className="quizz_wrapper">
      <div className="quizz_container">
        <h1>Quizz</h1>
        <div className="quizz_slides">
          <Slide
            data={inputData}
            currentSlide={slide}
            updateAnswer={updateAnswer}
          />

          <div className="navigation">
            <div
              className={`arrow arrow_left ${slide === 0 ? "inactive" : ""}`}
              onClick={() => (slide > 0 ? setSlide(slide - 1) : "")}
            ></div>
            <div
              className={`arrow arrow_right ${
                slide === inputData.length ? "inactive" : ""
              }`}
              onClick={() =>
                slide < inputData.length ? setSlide(slide + 1) : ""
              }
            ></div>
            <div className="progress_bar">
              <div className="fill" style={fill}></div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Quizz;
