import { React, useState } from "react";

const Slide = ({ data, currentSlide }) => {
  const { answers, question } = data[currentSlide];
  const [userAnswer, setUserAnswer] = useState(data[currentSlide]);

  const updateAnswer = (index) => {
    const newObj = data;
    newObj[currentSlide].answer = index;
    setUserAnswer(newObj[currentSlide]);
  };

  const Answers = () => {
    return (
      <ul>
        {answers.map((answer, index) => (
          <li
            className={`choice ${
              userAnswer.answer === index ? "selected" : ""
            }`}
            onClick={() => updateAnswer(index)}
            key={index}
          >
            {answer}
          </li>
        ))}
      </ul>
    );
  };

  return (
    <div className="slide">
      <h3>{question}</h3>
      <Answers />
    </div>
  );
};

export default Slide;
