import { React, useState } from "react";
import ReactDom from "react-dom";
import "./index.css";

const attrIbutes = [
  {
    id: 1,
    title: "title 1",
    author: "author text",
    img: "https://m.media-amazon.com/images/I/61JSKZ6vHQS._AC_UY218_.jpg",
  },
  {
    id: 2,
    title: "title 2",
    author: "author text",
    img: "https://m.media-amazon.com/images/I/61JSKZ6vHQS._AC_UY218_.jpg",
  },
  {
    id: 3,
    title: "title string",
    author: "author text",
    img: "https://m.media-amazon.com/images/I/61JSKZ6vHQS._AC_UY218_.jpg",
  },
];

function BookList() {
  const [books, setBooks] = useState(attrIbutes);

  const removeItem = (id) => {
    const newBooks = books.filter((item) => item.id !== id);
    setBooks(newBooks);
  };

  return (
    <section className="booklist">
      {attrIbutes.map(({ id, img, title, author }, index) => (
        // <Book key={index} {...el} />
        <article className="book">
          <img src={img} alt={title} onClick={() => alert(title)} />
          <h1
            onMouseOut={(e) => e.target.classList.remove("eee")}
            onMouseEnter={(e) => e.target.classList.add("eee")}
          >
            {title}
          </h1>
          <h4>{author}</h4>
          <button onClick={() => removeItem(id)}>Delete book #{id}</button>
        </article>
      ))}
    </section>
  );
}

