import { React, useState } from "react";
import reactDom from "react-dom";

function BookList() {
  const [persons, setNames] = useState({
    name: "Name",
    age: 24,
    message: "random message",
  });

  const removeItem = (name) =>
    setNames(persons.filter((person) => person.name !== name));

  return (
    <section>
      {persons.map(({ name, age, message }, index) => (
        <article key={index} className="book">
          <h1>{name}</h1>
          <h4>{age}</h4>
          <p>{message}</p>
          <button onClick={() => removeItem(name)}>Remove name {name}</button>
        </article>
      ))}
    </section>
  );
}

ReactDom.render(<BookList />, document.getElementById("root"));
