import { React, useState } from "react";

const Slide = (props) => {
  const { answers, question } = props.data;
  const [userAnswer, setUserAnswer] = useState("");

  const Answers = () => {
    return (
      <ul>
        {answers.map((answer, index) => (
          <li
            className={`choice ${userAnswer === index ? "selected" : ""}`}
            onClick={() => setUserAnswer(index)}
            key={index}
          >
            {answer}
          </li>
        ))}
      </ul>
    );
  };

  return (
    <div className="slide">
      <h3>{question}</h3>
      <Answers />
    </div>
  );
};

export default Slide;
