import { React, useState, useReducer } from "react";
import ReactDom from "react-dom";
import Final from "./final";

const data = [
  { id: 1, name: "John", age: 24 },
  { id: 2, name: "Doe", age: 31 },
];

const reducer = (state, { type, payload, closeModal }) => {
  if (type === "ADD_PERSON") {
    return {
      ...state,
      people: [...state.people, payload],
      closeModal,
    };
  }

  if (type === "CLOSE_MODAL") {
    return {
      ...state,
      closeModal: false,
    };
  }

  return state;
};

const defaultState = {
  people: data,
  content: "This is the content",
  closeModal: true,
};

function Users() {
  const [name, setName] = useState("");
  const [age, setAge] = useState();

  const [state, dispatch] = useReducer(reducer, defaultState);

  const handleSubmit = (e) => {
    e.preventDefault();

    name &&
      age &&
      dispatch({
        type: "ADD_PERSON",
        payload: { id: state.people.length + 1, name: name, age: age },
        closeModal: true,
      });
    setName("");
    setAge("");
  };

  const closeModal = () => {
    console.log("dispaching...");
    dispatch({ type: "CLOSE_MODAL" });
  };
  console.log("guesss" + state.closeModal);
  return (
    <>
      <h3>Use Reducer</h3>
      {state.closeModal && (
        <Final content={state.content} closeModal={closeModal}>
          <p>lorem ipsum</p>
        </Final>
      )}

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          value={name}
          onChange={(e) => setName(() => e.target.value)}
        />
        <input
          type="text"
          name="age"
          value={age ? age : ""}
          onChange={(e) => setAge(() => e.target.value)}
        />
        <input type="submit" value="Save Person" />
      </form>
      <ul>
        {state.people.map((person) => {
          return (
            <li key={person.id}>
              {person.name} has {person.age} years
            </li>
          );
        })}
      </ul>
    </>
  );
}

ReactDom.render(<Users />, document.getElementById("root"));
