import { React } from "react";

const Navigation = ({
  slide,
  prevSlide,
  dataLength,
  answerType,
  nextSlide,
}) => {
  return (
    <>
      <div
        className={`arrow arrow_left ${slide === 0 ? "inactive" : ""}`}
        onClick={prevSlide}
      ></div>
      <div
        className={`arrow arrow_right ${
          slide === dataLength || answerType === "undefined" ? "inactive" : ""
        }`}
        onClick={nextSlide}
      ></div>
      <div
        className={`arrow arrow_right ${
          slide === dataLength || !userAnswer[slide].answer ? "inactive" : ""
        }`}
        onClick={nextSlide}
      ></div>
    </>
  );
};

export default Navigation;
